window.APP = {
    lang: "ru",
    toogleLang: function () { this.lang = "en" === this.lang ? "ru" : "en"; var t = new URLSearchParams(window.location.search); t.set("lang", this.lang), window.history.replaceState(null, null, "?" + t.toString()),  this.setText(this)},
    setText: function (t) { var e = document.querySelector(".lang-image");
        document.getElementById("content-ru").classList.toggle("hide");
        document.getElementById("content-en").classList.toggle("hide");
        document.querySelector(".lang").innerHTML = "ru" === this.lang ? "RUS" : "ENG";
        e.classList.remove("ru", "en");
        e.classList.toggle(this.lang);
    },
}